import React from 'react';
import tarot2 from '../img/tarot2.png';

function Card() {
  return (
    <div className="flex justify-center">
      <div className="bg-principal rounded-3xl p-2 w-72 relative">
        <div
          style={{ backgroundImage: `url(${tarot2})` }}
          className="min-h-96 rounded-2xl flex justify-center items-center bg-no-repeat bg-cover bg-center relative"
        >
          {/* Capa adicional para el efecto de brillo en la imagen */}
          <div className="absolute inset-0 bg-black opacity-50 brightness-50 rounded-2xl w- "></div>

          {/* Contenido de texto */}
          <div className="absolute left-0 p-3 top-48 z-10">
            <h3 className="text-white text-3xl">Lectura Básica</h3>
          </div>
          <div className="absolute bottom-0 right-0 p-3 m-2 backdrop-blur-md rounded-2xl z-10">
            <h3 className="text-white text-2xl text-right">$1500</h3>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Card;
