function card() {
  return (
    <div className='bg-principal rounded-3xl p-2 '>
        {/* <div>
            <img className='rounded-2xl brightness-50 min-h-80 ' src={tarot2} alt="Foto de carta del tarot" />
        </div> */}
        <div style={{ backgroundImage: `url(${tarot1})` }} className='rounded-2xl min-h-80 flex justify-center items-center'>
            <div className='backdrop-blur-sm w-4/5 p-2 mt-10 '>
                <h3 className='text-white text-2xl ' >Lectura Básica</h3>
                <h3>$150</h3>
            </div>
        </div>

    </div>
  )
}

export default card
