import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";

import React from "react";
import { useLocation, useNavigate } from 'react-router-dom';

import Slider from "react-slick";
import Card from "./card";

import tarot1 from '../img/tarot1.png';
import tarot2 from '../img/tarot2.png';

function Responsive() {
  const navigate = useNavigate();
  const location = useLocation();
  const { cardsData = [] } = location.state || {}; // Desestructurar datos y asignar valores predeterminados

  const settings = {
    dots: false,
    infinite: true,
    speed: 500,
    slidesToShow: 3,
    slidesToScroll: 1,
    initialSlide: 0,
    responsive: [
      {
        breakpoint: 1134,
        settings: {
          slidesToShow: 2,
          slidesToScroll: 1,
          infinite: true,
          dots: true
        }
      },
      {
        breakpoint: 750,
        settings: {
          slidesToShow: 1,
          slidesToScroll: 1,
          initialSlide: 2
        }
      }
    ]
  };



  const handleCardClick = (card) => {
    navigate("/details", { state: { ...card } });
  };

  return (
    <div className="slider-container">
      <Slider {...settings}>
        {cardsData.map((card, index) => (
          <div key={index} className="flex justify-center">
            <Card
              {...card} // Pasa todos los atributos de la tarjeta al componente Card
              onClick={() => handleCardClick(card)}
            />
          </div>
        ))}
      </Slider>
    </div>
  );
}

export default Responsive;
