import React from 'react';
import Carousel from './Carousel';
import tarot1 from '../img/tarot1.png';
import tarot2 from '../img/tarot2.png';

// Datos organizados por categorías
const sectionsData = [
  {
    title: "Lecturas de Tarot",
    bgColor: "bg-gold",
    textColor: "text-principal",
    cardsData: [
      {
        backgroundImage: tarot1,
        title: "Lectura si o no",
        price: "$100",
        color: "#232323",
        detail: "La lectura de si y no tiene un lapso de 24 hrs...",
        included: ["Pregunta sí o no"],
        notIncluded: ["Explicaciones detalladas"],
        duration: "5 minutos"
      },
      {
        backgroundImage: tarot2,
        title: "Lectura si o no con explicación",
        price: "$200",
        color: "#232323",
        detail: "Lectura si o no con explicaciones adicionales...",
        included: ["Pregunta sí o no", "Explicación detallada"],
        notIncluded: ["Revisión de más de una pregunta"],
        duration: ""
      },
    ],
  },
  {
    title: "Rituales",
    bgColor: "bg-principal",
    textColor: "text-gold",
    cardsData: [
      {
        backgroundImage: tarot1,
        title: "Ritual de protección",
        price: "$300",
        color: "#232323",
        detail: "Este ritual protege de malas energías...",
        included: ["Protección energética"],
        notIncluded: [],
        duration: "30 minutos"
      },
    ],
  },
  {
    title: "Rituales de pareja/amor",
    bgColor: "bg-gold",
    textColor: "text-principal",
    cardsData: [
      {
        backgroundImage: tarot2,
        title: "Ritual de pareja",
        price: "$450",
        color: "#232323",
        detail: "Un ritual para armonizar relaciones amorosas...",
        included: ["Armonización energética"],
        notIncluded: [],
        duration: "1 hora"
      },
    ],
  },
];

function Ritual() {
  return (
    <div className="flex flex-col">
      {sectionsData.map((section, index) => (
        <div
          key={index}
          className={`flex ${section.bgColor} mt-9 justify-center py-2 flex-col items-center`}
        >
          <div className="w-4/5 flex justify-center flex-col items-left">
            <h2 className={`text-5xl ${section.textColor} font-semibold`}>{section.title}</h2>
          </div>
          <div className="w-4/5 mt-5">
            <Carousel cardsData={section.cardsData} />
          </div>
        </div>
      ))}
    </div>
  );
}

export default Ritual;
